import SectionTitle from "./SectionTitle"
import { useState } from "react"

function Experience() {
    const [activeTab, setActiveTab] = useState(0)

    const handleTabs = (index) => {
        setActiveTab(index)
    }

    const TabMenu = ({index, children, classes, onclick}) => {
        return(
            <div className="tab-item">
                <button type="button" onClick={onclick} className={classes}>{children}</button>
            </div>
        )
    }

    const TabPanel = ({index, children,}) => {
        return(
            <div className="tab-panel" index={index}>
                {children}
            </div>
        )
    }

    return (
        <div className="container mx-auto">
            <SectionTitle>Experience</SectionTitle>
            <div className="Tab">
                <div className="tab-menu">
                    <TabMenu index="0" onclick={() => handleTabs(0)}>Click Me</TabMenu>
                    <TabMenu index="1" onclick={() => handleTabs(0)}>Click Me</TabMenu>
                    <TabMenu index="2" onclick={() => handleTabs(0)}>Click Me</TabMenu>
                </div>
            </div>
            {
                <TabPanel></TabPanel>
            }
        </div>
    )
}

export default Experience
