import SectionTitle from "./SectionTitle"
import { useState } from "react"

const tabs = [
    {
        id: 0,
        title: "Spechy",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod.",
        position: "Frontend Developer",
        date: "2022 - Present",
    },
    {
        id: 1,
        title: "Luna Software",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod.",
        position: "Frontend Developer",
        date: "2021 - 2022",
    },
    {
        id: 2,
        title: "Temajet",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod.",
        position: "Frontend Developer",
        date: "2020 - 2021",
    },
]

function Experience() {
    const [activeTab, setActiveTab] = useState(0)

    const handleTabs = (index) => {
        setActiveTab(index)
    }

    const TabMenu = ({index, children, classes, onclick}) => {
        return(
            <div className={`tab-item border-b-4 inline-block py-2 px-4 ${index == activeTab && "border-indigo-700"}`}>
                <button type="button" onClick={onclick} className={classes}>{children}</button>
            </div>
        )
    }

    const TabPanel = ({index, children}) => {
        return(
            <div className="tab-panel py-3" index={index}>
                {children}
            </div>
        )
    }

    return (
        <div className="container mx-auto">
            <SectionTitle>Experience</SectionTitle>
            <div className="Tab flex">
                <div className="tab-menu">
                    <TabMenu index="0" onclick={() => handleTabs(0)}>Tab 0</TabMenu>
                    <TabMenu index="1" onclick={() => handleTabs(1)}>Tab 1</TabMenu>
                    <TabMenu index="2" onclick={() => handleTabs(2)}>Tab 2</TabMenu>
                </div>
            </div>
            {
                activeTab === 0 && <TabPanel index="0">Tab 0</TabPanel>
            }
            {
                activeTab === 1 && <TabPanel index="0">Tab 1</TabPanel>
            }
            {
                activeTab === 2 && <TabPanel index="0">Tab 2</TabPanel>
            }
        </div>
    )
}

export default Experience
