import SectionTitle from "./SectionTitle"
import { useState } from "react"

const tabs = [
    {
        title: "Spechy",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod. Tab 1",
        position: "Frontend Developer",
        date: "2022 - Present",
    },
    {
        title: "Luna Software",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod. Tab 2",
        position: "Frontend Developer",
        date: "2021 - 2022",
    },
    {
        title: "Temajet",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod. Tab 3",
        position: "Frontend Developer",
        date: "2020 - 2021",
    },
]

function Experience() {
    const [activeTab, setActiveTab] = useState(0)

    const handleTabs = (index) => {
        setActiveTab(index)
    }

    const TabMenu = ({index, children, classes, onclick}) => {
        return(
            <div 
            onClick={onclick} 
            className={`tab-item border-b-4 cursor-pointer inline-block py-3 font-medium text-gray-700 px-4 
            ${index == activeTab && "border-indigo-500 !text-indigo-500"}`}>
                <span className={classes}>{children}</span>
            </div>
        )
    }

    const TabPanel = ({index, children}) => {
        return(
            <div className="tab-panel py-6" index={index}>
                {children}
            </div>
        )
    }

    const Chip = ({children}) => {
        return(
            <span className="inline-block bg-indigo-100 rounded-xl px-4 py-0.5 text-indigo-600 font-medium mb-2">
                {children}
            </span>
        )
    }

    return (
        <div className="container mx-auto">
            <SectionTitle>Experience</SectionTitle>
            <div className="tab-items">
                {
                    tabs.map((tab, index) => {
                        return(
                            <TabMenu index={index} onclick={() => handleTabs(index)}>{tab.title}</TabMenu>
                        )
                    })
                }
            </div>
            <div className="tab-panels">
                {
                    tabs.map((tab, index) => {
                        return(
                            activeTab == index &&
                            <TabPanel index={index}>
                                <h3 className="text-2xl font-semibold text-gray-800 mb-2">{tab.title}</h3>
                                <div><Chip>{tab.position}</Chip></div>
                                <p className="mb-3">{tab.description}</p>
                                <div class="text-xs text-gray-600 font-medium">{tab.date}</div>
                            </TabPanel>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Experience
