import SectionTitle from "./SectionTitle"
import { useState } from "react"

const tabs = [
    {
        title: "Spechy",
        description: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
        position: "Frontend Developer",
        date: "2022 - Present",
    },
    {
        title: "Luna Software",
        description: "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English. Many desktop publishing packages and web page editors now use Lorem Ipsum as their default model text, and a search for 'lorem ipsum' will uncover many web sites still in their infancy. Various versions have evolved over the years, sometimes by accident, sometimes on purpose (injected humour and the like).",
        position: "Frontend Developer",
        date: "2021 - 2022",
    },
    {
        title: "Temajet",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod. Tab 3",
        position: "Frontend Developer",
        date: "2020 - 2021",
    },
]

function Experience() {
    const [activeTab, setActiveTab] = useState(0)

    const handleTabs = (index) => {
        setActiveTab(index)
    }

    const TabMenu = ({ index, children, classes, onclick }) => {
        return (
            <div
                onClick={onclick}
                className={`tab-item border-r-4 cursor-pointer inline-block py-3 font-medium text-gray-700 px-4 
            ${index == activeTab && "border-indigo-500 !text-indigo-500"}`}>
                <span className={classes}>{children}</span>
            </div>
        )
    }

    const TabPanel = ({ index, children }) => {
        return (
            <div className="tab-panel px-6" index={index}>
                {children}
            </div>
        )
    }

    const Chip = ({ children }) => {
        return (
            <span className="inline-block bg-indigo-100 rounded-xl px-4 py-0.5 text-indigo-600 font-medium mb-2">
                {children}
            </span>
        )
    }

    return (
        <div className="container mx-auto">
            <SectionTitle>Experience</SectionTitle>
            <div className="Tabs flex">
                <div className="tab-items w-[25%] flex flex-col">
                    {
                        tabs.map((tab, index) => {
                            return (
                                <TabMenu index={index} onclick={() => handleTabs(index)}>{tab.title}</TabMenu>
                            )
                        })
                    }
                </div>
                <div className="tab-panels">
                    {
                        tabs.map((tab, index) => {
                            return (
                                activeTab == index &&
                                <TabPanel index={index}>
                                    <h3 className="text-2xl font-semibold text-gray-800 mb-2">{tab.title}</h3>
                                    <div><Chip>{tab.position}</Chip></div>
                                    <p className="mb-3 text-gray-900">{tab.description}</p>
                                    <div class="text-xs text-gray-600 font-medium">{tab.date}</div>
                                </TabPanel>
                            )
                        })
                    }
                </div>
            </div>
        </div>
    )
}

export default Experience
