import { useState, useEffect } from "react"

function Experience() {
    const [value, setValue] = useState(0)

    useEffect(() => {
        setValue(0)
    }, [])

    const handleTabs = () => {
        setValue()
    }

    useEffect(() => {
        console.log(value)
    }, value)

    return (
        <div className="container mx-auto">
            <SectionTitle>Experience</SectionTitle>
            <div className="Tab">
                <div className="tab-menu">
                    <div className="tab-item">
                        <button type="button" value="0" onClick={handleTabs}>Tab One</button>
                    </div>
                    <div className="tab-item">
                        <button type="button" value="1" onClick={handleTabs}>Tab Two</button>
                    </div>
                </div>
            </div>
            <div className="tab-panel" aria-value="0">
                Item One
            </div>
            <div className="tab-panel" aria-value="1">
                Item Two
            </div>
        </div>
    )
}

export default Experience
