import SectionTitle from "./SectionTitle"
import { useState } from "react"

const tabs = [
    {
        title: "Spechy",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod.",
        position: "Frontend Developer",
        date: "2022 - Present",
    },
    {
        title: "Luna Software",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod.",
        position: "Frontend Developer",
        date: "2021 - 2022",
    },
    {
        title: "Temajet",
        description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quod.",
        position: "Frontend Developer",
        date: "2020 - 2021",
    },
]

function Experience() {
    const [activeTab, setActiveTab] = useState(0)

    const handleTabs = (index) => {
        setActiveTab(index)
    }

    const TabMenu = ({index, children, classes, onclick}) => {
        return(
            <div 
            onClick={onclick} 
            className={`tab-item border-b-4 cursor-pointer inline-block py-2 px-4 
            ${index == activeTab && "border-indigo-500"}`}>
                <span className={classes}>{children}</span>
            </div>
        )
    }

    const TabPanel = ({index, children}) => {
        return(
            <div className="tab-panel py-3" index={index}>
                {children}
            </div>
        )
    }

    return (
        <div className="container mx-auto">
            <SectionTitle>Experience</SectionTitle>
            <div className="tab-items">
                {
                    tabs.map((tab, index) => {
                        return(
                            <TabMenu index={index} onclick={() => handleTabs(index)}>{tab.title}</TabMenu>
                        )
                    })
                }
            </div>
            <div className="tab-panels">
                {
                    tabs.map((tab, index) => {
                        return(
                            <TabPanel index={index}>{tab.description}</TabPanel>
                        )
                    })
                }
            </div>
            {
                activeTab === 0 && <TabPanel index="0">Tab 0</TabPanel>
            }
            {
                activeTab === 1 && <TabPanel index="0">Tab 1</TabPanel>
            }
            {
                activeTab === 2 && <TabPanel index="0">Tab 2</TabPanel>
            }
        </div>
    )
}

export default Experience
